
public class SwitchReturn {

	public static void main(String[] args) {
		int day=1;
		String switchReturnValue=switch(day) {
		case 1->"monday";
		case 2->"tuesday";
		default->"Not available";
		
		};
		System.out.println(switchReturnValue);

		// TODO Auto-generated method stub

	}

}
